import { spawn } from 'child_process'
import {
  existsSync,
  mkdirSync,
  readFileSync,
  unlinkSync,
  writeFileSync,
} from 'fs'
import os from 'os'
import path from 'path'
import { Interface } from 'readline'

import {
  FileChanges,
  FileChangeSchema,
  InitResponseSchema,
  MessageCostResponseSchema,
  PromptResponseSchema,
