    if (!isWindows) {
      persistentPty.write(`PS1=${promptIdentifier} && PS2=${promptIdentifier}\n`)
    }

    return { type: 'pty', shell: 'pty', pty: persistentPty, timerId: null }
  } else {
    // Fallback to child_process
    const isWindows = os.platform() === 'win32'
    const currShell = detectShell()
    const shell = isWindows
      ? currShell === 'powershell'
        ? 'powershell.exe'
        : 'cmd.exe'
      : 'bash'
    const childProcess = null as ChildProcessWithoutNullStreams | null
    return {
      type: 'process',
      shell,
      childProcess,
      timerId: null,
    }
  }
}

export let persistentProcess: ReturnType<
  typeof createPersistantProcess
> | null = null

process.stdout.on('resize', () => {
  if (!persistentProcess) return
  if (persistentProcess.type === 'pty') {
    persistentProcess.pty.resize(process.stdout.columns, process.stdout.rows)
  }
})

let commandIsRunning = false

export const isCommandRunning = () => {
  return commandIsRunning
}

export const recreateShell = (cwd: string) => {
  persistentProcess = createPersistantProcess(cwd)
}

export const resetShell = (cwd: string) => {
  commandIsRunning = false
  if (persistentProcess) {
    if (persistentProcess.timerId) {
      clearTimeout(persistentProcess.timerId)
      persistentProcess.timerId = null
    }

    if (persistentProcess.type === 'pty') {
      persistentProcess.pty.kill()
      recreateShell(cwd)
    } else {
      persistentProcess.childProcess?.kill()
      persistentProcess = {
        ...persistentProcess,
        childProcess: null,
      }
    }
  }
}

function formatResult(command: string, stdout: string, status: string, exitCode: number | null = null): string {
  return buildArray(
    `<command>${command}</command>`,
    '<terminal_command_result>',
    `<output>${truncateStringWithMessage({ str: stdout, maxLength: COMMAND_OUTPUT_LIMIT, remove: 'MIDDLE' })}</output>`,
    `<status>${status}</status>`,
    exitCode !== null && `<exit_code>${exitCode}</exit_code>`,
    '</terminal_command_result>'
  ).join('\n')
}

const MAX_EXECUTION_TIME = 30_000

export function runBackgroundCommand(
  options: {
    toolCallId: string
    command: string
    mode: 'user' | 'assistant'
    cwd: string
    stdoutFile?: string
    stderrFile?: string
  },
  resolveCommand: (value: {
    result: string
    stdout: string
    exitCode: number | null
  }) => void
): void {
  const { toolCallId, command, mode, cwd, stdoutFile, stderrFile } = options
  const isWindows = os.platform() === 'win32'
  const shell = isWindows ? 'cmd.exe' : 'bash'
  const shellArgs = isWindows ? ['/c'] : ['-c']

  if (mode === 'assistant') {
    console.log(green(`Running background process...\n> ${command}`))
  }

  const initialStdout = ''
  const initialStderr = ''

  try {
    const childProcess = spawnAndTrack(shell, [...shellArgs, command], {
      cwd,
      env: { ...process.env, FORCE_COLOR: '1' },
      // Ensure detached is always false to link child lifetime to parent
      detached: false,
      stdio: 'pipe',
    })

    // An error should have been thrown when we called `spawn`
    assert(
      childProcess.pid !== undefined,
      'Failed to spawn process: no PID assigned.'
    )

    const processId = childProcess.pid
    const processInfo: BackgroundProcessInfo = {
      pid: processId,
      toolCallId,
      command,
      process: childProcess,
      stdoutBuffer: [],
      stderrBuffer: [],
      status: 'running',
      startTime: Date.now(),
      endTime: null,
      lastReportedStdoutLength: 0,
      lastReportedStderrLength: 0,
      lastReportedStatus: null,
      stdoutFile,
      stderrFile,
    }
    backgroundProcesses.set(processId, processInfo)

    // Set up file streams if paths are provided
    let stdoutStream: WriteStream | undefined
    let stderrStream: WriteStream | undefined

    if (stdoutFile) {
      const stdoutAbs = path.isAbsolute(stdoutFile)
        ? stdoutFile
        : path.join(cwd, stdoutFile)
      mkdirSync(dirname(stdoutAbs), { recursive: true })
      stdoutStream = createWriteStream(stdoutAbs)
    }

    const realStderrFile = stderrFile || stdoutFile
    if (realStderrFile) {
      const stderrAbs = path.isAbsolute(realStderrFile)
        ? realStderrFile
        : path.join(cwd, realStderrFile)
      mkdirSync(dirname(stderrAbs), { recursive: true })
      stderrStream = createWriteStream(stderrAbs)
    }

    childProcess.stdout.on('data', (data: Buffer) => {
      const output = stripColors(data.toString())
      processInfo.stdoutBuffer.push(output)

      // Write to file if stream exists
      if (stdoutStream) {
        stdoutStream.write(output)
      }
    })

    childProcess.stderr.on('data', (data: Buffer) => {
      const output = stripColors(data.toString())
      processInfo.stderrBuffer.push(output)

      // Write to file if stream exists
      if (stderrStream) {
        stderrStream.write(output)
      }
    })

    childProcess.on('error', (error) => {
      processInfo.status = 'error'
      processInfo.stderrBuffer.push(
        `\nError spawning command: ${error.message}`
      )
      processInfo.endTime = Date.now()

      // Close file streams
      stdoutStream?.end()
      stderrStream?.end()
    })

    childProcess.on('close', (code) => {
      processInfo.status = code === 0 ? 'completed' : 'error'
      processInfo.endTime = Date.now()

      // Close file streams
      stdoutStream?.end()
      stderrStream?.end()
    })

    // Unreference the process so the parent can exit independently IF the child is the only thing keeping it alive.
    childProcess.unref()

    const resultMessage = `<background_process>
<process_id>${processId}</process_id>
<command>${command}</command>
<status>${processInfo.status}</status>
</background_process>`
    resolveCommand({
      result: resultMessage,
      stdout: initialStdout + initialStderr,
      exitCode: null,
    })
  } catch (error: any) {
    const errorMessage = `<background_process>\n<command>${command}</command>\n<error>${error.message}</error>\n</background_process>`
    resolveCommand({
      result: errorMessage,
      stdout: error.message,
      exitCode: null,
    })
  }
}

export const runTerminalCommand = async (
  toolCallId: string,
  command: string,
  mode: 'user' | 'assistant',
  processType: 'SYNC' | 'BACKGROUND',
  stdoutFile?: string,
  stderrFile?: string
): Promise<{ result: string; stdout: string }> => {
  const cwd = mode === 'assistant' ? getProjectRoot() : getWorkingDirectory()
  return new Promise((resolve) => {
    if (!persistentProcess) {
      throw new Error('Shell not initialized')
    }

    if (commandIsRunning) {
      resetShell(cwd)
    }

    commandIsRunning = true

    // Add special case for git log to limit output
    const modifiedCommand =
      command.trim() === 'git log' ? 'git log -n 5' : command

    const resolveCommand = (value: {
      result: string
      stdout: string
      exitCode: number | undefined
    }) => {
      commandIsRunning = false
      trackEvent(AnalyticsEvent.TERMINAL_COMMAND_COMPLETED, {
        command,
        result: value.result,
        stdout: value.stdout,
        exitCode: value.exitCode,
        mode,
        processType,
      })
      resolve(value)
    }

    if (processType === 'BACKGROUND') {
      runBackgroundCommand(
        {
          toolCallId,
          command: modifiedCommand,
          mode,
          cwd,
          stdoutFile,
          stderrFile,
        },
        resolveCommand
      )
    } else if (persistentProcess.type === 'pty') {
      runCommandPty(
        persistentProcess,
        modifiedCommand,
        mode,
        resolveCommand,
        cwd
      )
    } else {
      // Fallback to child_process implementation
      runCommandChildProcess(
        persistentProcess,
        modifiedCommand,
        mode,
        resolveCommand,
        cwd
      )
    }
  })
}

function handleChangeDirectory(
  mode: 'user' | 'assistant',
  command: string,
  ptyProcess: IPty,
  cwd: string
): boolean {
  if (!command.startsWith('cd ')) {
    return false
  }
  if (mode === 'assistant') {
    return false
  }

  let newWorkingDirectory = command.split(' ')[1]
  if (newWorkingDirectory === '~') {
    newWorkingDirectory = os.homedir()
  } else if (newWorkingDirectory.startsWith('~/')) {
    newWorkingDirectory = path.join(os.homedir(), newWorkingDirectory.slice(2))
  } else if (!path.isAbsolute(newWorkingDirectory)) {
    newWorkingDirectory = path.join(cwd, newWorkingDirectory)
  }

  trackEvent(AnalyticsEvent.CHANGE_DIRECTORY, {
    from: cwd,
    to: newWorkingDirectory,
    isSubdir: !path.relative(cwd, newWorkingDirectory).startsWith('..'),
  })
  const projectRoot = getProjectRoot()
  if (path.relative(projectRoot, newWorkingDirectory).startsWith('..')) {
    console.log(`
Unable to cd outside of the project root (${projectRoot})

If you want to change the project root:
1. Exit Codebuff (type "exit").
2. Navigate into the target directory.
3. Restart Codebuff.`)
    return true
  }

  if (isDir(newWorkingDirectory)) {
    setWorkingDirectory(newWorkingDirectory)
    ptyProcess.write(`cd ${newWorkingDirectory}\r`)
    return true
  }

  return false
}

export const runCommandPty = (
  persistentProcess: PersistentProcess & {
    type: 'pty'
  },
  command: string,
  mode: 'user' | 'assistant',
  resolve: (value: {
    result: string
    stdout: string
    exitCode: number | null
  }) => void,
  cwd: string
) => {
  const ptyProcess = persistentProcess.pty

  if (handleChangeDirectory(mode, command, ptyProcess, cwd)) {
    resolve({
      result: formatResult(command, '', 'complete', 0),
      stdout: '',
      exitCode: 0,
    })
    return
  }

  if (command.trim() === 'clear') {
    // `clear` needs access to the main process stdout. This is a workaround.
    execSync('clear', { stdio: 'inherit' })
    resolve({
      result: formatResult(command, '', 'complete', 0),
      stdout: '',
      exitCode: 0,
    })
    return
  }

  if (mode === 'assistant') {
    console.log(green(`> ${command}`))
  }

  let commandOutput = ''
  let buffer = promptIdentifier
  let echoLinesRemaining = os.platform() === 'win32' ? 1 : command.split('\n').length

  const timer = setTimeout(() => {
    if (mode === 'assistant') {
      // Kill and recreate PTY
      resetShell(cwd)

      resolve({
        result: formatResult(
          command,
          commandOutput,
          `Command timed out after ${MAX_EXECUTION_TIME / 1000} seconds and was terminated. Shell has been restarted.`,
          124
        ),
        stdout: commandOutput,
        exitCode: 124,
      })
    }
  }, MAX_EXECUTION_TIME)

  persistentProcess.timerId = timer

  const longestSuffixThatsPrefixOf = (str: string, target: string): string => {
    for (let len = target.length; len > 0; len--) {
      const prefix = target.slice(0, len)
      if (str.endsWith(prefix)) {
        return prefix
      }
    }

    return ''
  }

  const toRemovePattern = new RegExp(`${promptIdentifier}[^\n]*\n`, 'g')

  const dataDisposable = ptyProcess.onData((data: string) => {
    buffer += data
    const suffix = longestSuffixThatsPrefixOf(buffer, promptIdentifier)
    let toProcess = buffer.slice(0, buffer.length - suffix.length)
    buffer = suffix

    const matches = toProcess.match(toRemovePattern)
    if (matches) {
      echoLinesRemaining -= matches.length
      echoLinesRemaining = Math.max(echoLinesRemaining, 0)
    }

    // Process normal output line
    toProcess = toProcess.replaceAll(toRemovePattern, '')
    let commandCompleted = buffer === promptIdentifier

    // Check for completion messages
    if (toProcess.includes(`${promptIdentifier}Command completed.`) || toProcess.includes(`${promptIdentifier}Command failed with exit code`)) {
      toProcess = toProcess.replaceAll(promptIdentifier, '')
      commandCompleted = true
    }

    if (toProcess.includes(promptIdentifier)) {
      toProcess = toProcess.replaceAll(promptIdentifier, '')
      commandCompleted = true
    }
    process.stdout.write(toProcess)
    commandOutput += toProcess

    if (commandCompleted && echoLinesRemaining === 0) {
      // Command is done
      clearTimeout(timer)
      dataDisposable.dispose()

      // Extract exit code from command output
      let exitCode: number | null = null
      if (commandOutput.includes(`${promptIdentifier}Command completed.`)) {
        exitCode = 0
      } else {
        const match = commandOutput.match(new RegExp(`${promptIdentifier}Command failed with exit code (\\d+)\\.`))
        if (match) {
          exitCode = parseInt(match[1])
        }
      }

      // Reset the PTY to the project root
      ptyProcess.write(`cd ${getWorkingDirectory()}\r`)

      resolve({
        result: formatResult(command, commandOutput, 'complete', exitCode),
        stdout: commandOutput,
        exitCode,
      })
      return
    }
  })

  const isWindows = os.platform() === 'win32'

  // Write the command
  const commandWithCheck = isWindows
    ? `${command}\r\necho "${promptIdentifier}"`
    : `${command}; ec=$?; if [ $ec -eq 0 ]; then printf "${promptIdentifier}Command completed."; else printf "${promptIdentifier}Command failed with exit code $ec."; fi`
  ptyProcess.write(commandWithCheck + '\r')
}

const runCommandChildProcess = (
  persistentProcess: ReturnType<typeof createPersistantProcess> & {
    type: 'process'
  },
  command: string,
  mode: 'user' | 'assistant',
  resolve: (value: {
    result: string
    stdout: string
    exitCode: number | null
  }) => void,
  cwd: string
) => {
  const isWindows = os.platform() === 'win32'
  let commandOutput = ''

  if (mode === 'assistant') {
    console.log(green(`> ${command}`))
  }

  const childProcess = spawn(
    persistentProcess.shell,
    [isWindows ? '/c' : '-c', command],
    {
      cwd,
      env: {
        ...process.env,
        PAGER: 'cat',
        GIT_PAGER: 'cat',
        GIT_TERMINAL_PROMPT: '0',
        LESS: '-FRX',
      },
    }
  )
  persistentProcess = {
    ...persistentProcess,
    childProcess,
  }

  const timer = setTimeout(() => {
    resetShell(cwd)
    if (mode === 'assistant') {
      resolve({
        result: formatResult(
          command,
          commandOutput,
          `Command timed out after ${MAX_EXECUTION_TIME / 1000} seconds and was terminated.`,
          124
        ),
        stdout: commandOutput,
        exitCode: 124,
      })
    }
  }, MAX_EXECUTION_TIME)

  persistentProcess.timerId = timer

  childProcess.stdout.on('data', (data: Buffer) => {
    const output = data.toString()
    process.stdout.write(output)
    commandOutput += output
  })

  childProcess.stderr.on('data', (data: Buffer) => {
    const output = data.toString()
    process.stdout.write(output)
    commandOutput += output
  })

  childProcess.on('close', (code) => {
    clearTimeout(timer)

    if (command.startsWith('cd ') && mode === 'user') {
      const newWorkingDirectory = command.split(' ')[1]
      cwd = setWorkingDirectory(path.join(cwd, newWorkingDirectory))
    }

    if (mode === 'assistant') {
      console.log(green(`Command completed`))
    }

    resolve({
      result: formatResult(command, commandOutput, `complete`, code),
      stdout: commandOutput,
      exitCode: code,
    })
  })
}

export function killAndResetPersistentProcess() {
  if (persistentProcess?.type === 'pty') {
    persistentProcess.pty.kill()
    persistentProcess = null
  }
}

